targets.query:
from(bucket:"tig")
  |> range(start: -2m)
  |> filter(fn: (r) =>
    r._measurement == "router_asus" and
    r._field == "sysUpTime"
  )
//  |> map(fn: (r) => ({ r with _value: r._value * 1000000000 }))
//  |> map(fn: (r) => ({ r with _value: string(v: duration(v: r._value)) }))

from(bucket: "tig")
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) =>
        r._measurement == "router_asus" and
        (r._field == "connections_total" or r._field == "connections_active") 
        )
//    |> derivative(unit: 1s, nonNegative: true)
    |> drop(columns: ["host"])
    |> aggregateWindow(every: v.windowPeriod, fn: mean)

from(bucket: "tig")
    |> range(start: -3m)
    |> filter(fn: (r) =>
        r._measurement == "router_asus" and
        r._field == "temp_CPU"        
        )
//    |> drop(columns: ["container_version", "engine_host", "host", "server_version"])
//    |> aggregateWindow(every: 3m, fn: mean)

from(bucket: "tig")
    |> range(start: -3m)
    |> filter(fn: (r) =>
        r._measurement == "router_asus" and
        (r._field == "temp_2_4_GHz" or r._field == "temp_5_GHz")       
        )
    |> drop(columns: ["host"])
//    |> aggregateWindow(every: 3m, fn: mean)

from(bucket: "tig")
    |> range(start: -2m)
    |> filter(fn: (r) =>
        r._measurement == "router_asus" and
        r._field == "ipNetToMediaPhysAddress"    
        )
//  |> drop(columns: ["ipNetToMediaIfIndex", "host"])
  |> keep(columns: ["_value", "ipNetToMediaNetAddress", "_start", "_stop", "_time"])
  |> rename(columns: {ipNetToMediaNetAddress: "_field"})

from(bucket: "tig")
    |> range(start: -2m)
    |> filter(fn: (r) =>
        r._measurement == "router_asus" and
        r._field == "ifHighSpeed" and 
        (r.ifName == "eth0" or r.ifName == "eth1" or r.ifName == "eth2" or r.ifName == "eth3" or r.ifName == "eth4" or r.ifName == "eth5")
        )
//    |> derivative(unit: 1s, nonNegative: true)
    |> drop(columns: ["host", "agent_host"])    
//    |> aggregateWindow(every: 1m, fn: mean)
